import React, { useState, useEffect } from 'react';

function CharacterSheet() {
  const [data, setData] = useState({
    stats: { STR: '', DEX: '', CON: '', INT: '', WIS: '', CHA: '' },
    saves: { STR: '', DEX: '', CON: '', INT: '', WIS: '', CHA: '' },
    skills: { Acrobatics: '', Stealth: '', Perception: '', Arcana: '', Survival: '', Persuasion: '' },
    armorClass: '', initiative: '', speed: '', hitPoints: ''
  });

  const [showStats, setShowStats] = useState(true);
  const [showSaves, setShowSaves] = useState(false);
  const [showSkills, setShowSkills] = useState(false);
  const [showCombat, setShowCombat] = useState(false);

  const fetchCharacter = async () => {
    try {
      const res = await fetch('/api/character');
      const json = await res.json();
      if (json) setData(prev => ({ ...prev, ...json }));
    } catch (err) {
      console.error('Fetch error:', err);
    }
  };

  useEffect(() => {
    fetchCharacter();
  }, []);

  const handleChange = (group, key, value) => {
    setData(prev => ({
      ...prev,
      [group]: { ...prev[group], [key]: value }
    }));
  };

  const handleSave = async () => {
    try {
      const res = await fetch('/api/character', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });
      const result = await res.json();
      alert('Character saved!');
    } catch (err) {
      console.error('Save error:', err);
    }
  };

  const toggle = (setter, value) => () => setter(!value);

  return (
    <div style={{ display: 'flex', flexDirection: 'column', height: '100%' }}>
      <div style={{ flexGrow: 1, overflowY: 'auto', paddingBottom: '10px' }}>

        {/* STATS */}
        <h3 onClick={toggle(setShowStats, showStats)} style={{ cursor: 'pointer', userSelect: 'none' }}>
          {showStats ? '▼' : '▶'} Stats
        </h3>
        {showStats && (
          <div>
            {Object.entries(data.stats).map(([key, val]) => (
              <div key={key}>
                <label>{key}: </label>
                <input
                  type="number"
                  value={val}
                  onChange={e => handleChange('stats', key, e.target.value)}
                  style={{ width: '100%', marginBottom: '5px' }}
                />
              </div>
            ))}
          </div>
        )}

        {/* SAVING THROWS */}
        <h3 onClick={toggle(setShowSaves, showSaves)} style={{ cursor: 'pointer', userSelect: 'none' }}>
          {showSaves ? '▼' : '▶'} Saving Throws
        </h3>
        {showSaves && (
          <div>
            {Object.entries(data.saves).map(([key, val]) => (
              <div key={key}>
                <label>{key} Save: </label>
                <input
                  type="number"
                  value={val}
                  onChange={e => handleChange('saves', key, e.target.value)}
                  style={{ width: '100%', marginBottom: '5px' }}
                />
              </div>
            ))}
          </div>
        )}

        {/* SKILLS */}
        <h3 onClick={toggle(setShowSkills, showSkills)} style={{ cursor: 'pointer', userSelect: 'none' }}>
          {showSkills ? '▼' : '▶'} Skills
        </h3>
        {showSkills && (
          <div>
            {Object.entries(data.skills).map(([key, val]) => (
              <div key={key}>
                <label>{key}: </label>
                <input
                  type="number"
                  value={val}
                  onChange={e => handleChange('skills', key, e.target.value)}
                  style={{ width: '100%', marginBottom: '5px' }}
                />
              </div>
            ))}
          </div>
        )}

        {/* COMBAT */}
        <h3 onClick={toggle(setShowCombat, showCombat)} style={{ cursor: 'pointer', userSelect: 'none' }}>
          {showCombat ? '▼' : '▶'} Combat
        </h3>
        {showCombat && (
          <div>
            <label>Armor Class:</label>
            <input
              type="number"
              value={data.armorClass}
              onChange={e => setData(prev => ({ ...prev, armorClass: e.target.value }))}
              style={{ width: '100%', marginBottom: '5px' }}
            />
            <label>Initiative:</label>
            <input
              type="number"
              value={data.initiative}
              onChange={e => setData(prev => ({ ...prev, initiative: e.target.value }))}
              style={{ width: '100%', marginBottom: '5px' }}
            />
            <label>Speed:</label>
            <input
              type="text"
              value={data.speed}
              onChange={e => setData(prev => ({ ...prev, speed: e.target.value }))}
              style={{ width: '100%', marginBottom: '5px' }}
            />
            <label>Hit Points:</label>
            <input
              type="text"
              value={data.hitPoints}
              onChange={e => setData(prev => ({ ...prev, hitPoints: e.target.value }))}
              style={{ width: '100%', marginBottom: '5px' }}
            />
          </div>
        )}
      </div>

      <div style={{
        borderTop: '1px solid #ccc',
        padding: '10px 0',
        backgroundColor: '#f5f5f5'
      }}>
        <button onClick={handleSave} style={{ width: '100%' }}>
          Save Changes
        </button>
      </div>
    </div>
  );
}

export default CharacterSheet;
