import React, { useEffect, useRef, useState } from 'react';

function CharacterSheet() {
  const sheetRef = useRef();

  const [stats, setStats] = useState({ STR: '', DEX: '', CON: '', INT: '', WIS: '', CHA: '' });
  const [saves, setSaves] = useState({ STR: '', DEX: '', CON: '', INT: '', WIS: '', CHA: '' });
  const [skills, setSkills] = useState({ Acrobatics: '', Stealth: '', Perception: '', Arcana: '', Survival: '', Persuasion: '' });
  const [armorClass, setArmorClass] = useState('');
  const [initiative, setInitiative] = useState('');
  const [speed, setSpeed] = useState('');
  const [hitPoints, setHitPoints] = useState('');

  useEffect(() => {
    fetch('/api/character')
      .then(res => res.json())
      .then(data => {
        if (!data) return;
        setStats(data.stats || {});
        setSaves(data.saves || {});
        setSkills(data.skills || {});
        setArmorClass(data.armorClass || '');
        setInitiative(data.initiative || '');
        setSpeed(data.speed || '');
        setHitPoints(data.hitPoints || '');
      });
  }, []);

  const saveCharacter = async () => {
    const payload = {
      stats,
      saves,
      skills,
      armorClass,
      initiative,
      speed,
      hitPoints
    };
    await fetch('/api/character', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(payload)
    });
    alert('Character saved!');
  };

  return (
    <div style={{ fontFamily: 'Cinzel, serif' }}>
      <div ref={sheetRef} style={{ padding: '20px', backgroundColor: '#fff', border: '1px solid #ccc' }}>
        <h2 style={{ textAlign: 'center' }}>üßù Character Sheet</h2>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '10px' }}>
          {Object.keys(stats).map((stat) => (
            <div key={stat}>
              <label>{stat}</label>
              <input type="number" value={stats[stat]} onChange={e => setStats({ ...stats, [stat]: e.target.value })} style={{ width: '100%' }} />
            </div>
          ))}
        </div>

        <h4 style={{ marginTop: '20px' }}>üõ°Ô∏è Combat Stats</h4>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '10px' }}>
          <div><label>Armor Class</label><input type="number" value={armorClass} onChange={e => setArmorClass(e.target.value)} style={{ width: '100%' }} /></div>
          <div><label>Initiative</label><input type="number" value={initiative} onChange={e => setInitiative(e.target.value)} style={{ width: '100%' }} /></div>
          <div><label>Speed</label><input type="number" value={speed} onChange={e => setSpeed(e.target.value)} style={{ width: '100%' }} /></div>
          <div><label>Hit Points</label><input type="text" value={hitPoints} onChange={e => setHitPoints(e.target.value)} style={{ width: '100%' }} /></div>
        </div>

        <h4 style={{ marginTop: '20px' }}>üéØ Saving Throws</h4>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '10px' }}>
          {Object.keys(saves).map((save) => (
            <div key={save + '-save'}>
              <label>{save} Save</label>
              <input type="number" value={saves[save]} onChange={e => setSaves({ ...saves, [save]: e.target.value })} style={{ width: '100%' }} />
            </div>
          ))}
        </div>

        <h4 style={{ marginTop: '20px' }}>üß† Skills</h4>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '10px' }}>
          {Object.keys(skills).map((skill) => (
            <div key={skill}>
              <label>{skill}</label>
              <input type="number" value={skills[skill]} onChange={e => setSkills({ ...skills, [skill]: e.target.value })} style={{ width: '100%' }} />
            </div>
          ))}
        </div>
      </div>

      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        <button onClick={saveCharacter} style={{ padding: '10px 20px', fontWeight: 'bold' }}>üíæ Save Changes</button>
      </div>
    </div>
  );
}

export default CharacterSheet;
