import React, { useState, useEffect } from 'react';

const skillMap = {
  STR: ['Athletics'],
  DEX: ['Acrobatics', 'Sleight of Hand', 'Stealth'],
  INT: ['Arcana', 'History', 'Investigation', 'Nature', 'Religion'],
  WIS: ['Animal Handling', 'Insight', 'Medicine', 'Perception', 'Survival'],
  CHA: ['Deception', 'Intimidation', 'Performance', 'Persuasion'],
};

function CharacterSheet() {
  const [data, setData] = useState({
    stats: { STR: '', DEX: '', CON: '', INT: '', WIS: '', CHA: '' },
    saves: { STR: '', DEX: '', CON: '', INT: '', WIS: '', CHA: '' },
    skills: Object.fromEntries(Object.values(skillMap).flat().map(k => [k, ''])),
    armorClass: '', initiative: '', speed: '', hitPoints: ''
  });

  const [showStats, setShowStats] = useState(true);
  const [showSaves, setShowSaves] = useState(false);
  const [showSkills, setShowSkills] = useState(false);
  const [showCombat, setShowCombat] = useState(false);
  const [skillToggles, setSkillToggles] = useState({});

  const toggleSkillGroup = (group) => {
    setSkillToggles(prev => ({ ...prev, [group]: !prev[group] }));
  };

  const fetchCharacter = async () => {
    try {
      const res = await fetch('/api/character');
      const json = await res.json();
      if (json) setData(prev => ({ ...prev, ...json }));
    } catch (err) {
      console.error('Fetch error:', err);
    }
  };

  useEffect(() => {
    fetchCharacter();
  }, []);

  const handleChange = (group, key, value) => {
    setData(prev => ({
      ...prev,
      [group]: { ...prev[group], [key]: value }
    }));
  };

  const handleSave = async () => {
    try {
      const res = await fetch('/api/character', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });
      await res.json();
      alert('Character saved!');
    } catch (err) {
      console.error('Save error:', err);
    }
  };

  const medievalFont = { fontFamily: 'Cinzel, serif' };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', height: '100%' }}>
      {/* Scrollable content */}
      <div style={{ flexGrow: 1, overflowY: 'auto', paddingBottom: '10px' }}>

        <h3 onClick={() => setShowStats(!showStats)} style={{ ...medievalFont, cursor: 'pointer' }}>
          {showStats ? '▼' : '▶'} Stats
        </h3>
        {showStats && (
          <div>
            {Object.entries(data.stats).map(([key, val]) => (
              <div key={key}>
                <label style={medievalFont}>{key}: </label>
                <input
                  type="number"
                  value={val}
                  onChange={e => handleChange('stats', key, e.target.value)}
                  style={{ width: '100%', marginBottom: '5px' }}
                />
              </div>
            ))}
          </div>
        )}

        <h3 onClick={() => setShowSaves(!showSaves)} style={{ ...medievalFont, cursor: 'pointer' }}>
          {showSaves ? '▼' : '▶'} Saving Throws
        </h3>
        {showSaves && (
          <div>
            {Object.entries(data.saves).map(([key, val]) => (
              <div key={key}>
                <label style={medievalFont}>{key} Save: </label>
                <input
                  type="number"
                  value={val}
                  onChange={e => handleChange('saves', key, e.target.value)}
                  style={{ width: '100%', marginBottom: '5px' }}
                />
              </div>
            ))}
          </div>
        )}

        <h3 onClick={() => setShowSkills(!showSkills)} style={{ ...medievalFont, cursor: 'pointer' }}>
          {showSkills ? '▼' : '▶'} Skills
        </h3>
        {showSkills && (
          <div>
            {Object.entries(skillMap).map(([ability, skills]) => (
              <div key={ability}>
                <h4 onClick={() => toggleSkillGroup(ability)} style={{ ...medievalFont, cursor: 'pointer', marginBottom: '5px' }}>
                  {skillToggles[ability] ? '▼' : '▶'} {ability}
                </h4>
                {skillToggles[ability] && (
                  <div style={{ paddingLeft: '20px' }}>
                    {skills.map(skill => (
                      <div key={skill}>
                        <label style={medievalFont}>{skill}:</label>
                        <input
                          type="number"
                          value={data.skills[skill]}
                          onChange={e => handleChange('skills', skill, e.target.value)}
                          style={{ width: '100%', marginBottom: '5px' }}
                        />
                      </div>
                    ))}
                  </div>
                )}
              </div>
            ))}
          </div>
        )}

        <h3 onClick={() => setShowCombat(!showCombat)} style={{ ...medievalFont, cursor: 'pointer' }}>
          {showCombat ? '▼' : '▶'} Combat Stats
        </h3>
        {showCombat && (
          <div>
            <label style={medievalFont}>Armor Class:</label>
            <input
              type="number"
              value={data.armorClass}
              onChange={e => setData(prev => ({ ...prev, armorClass: e.target.value }))}
              style={{ width: '100%', marginBottom: '5px' }}
            />
            <label style={medievalFont}>Initiative:</label>
            <input
              type="number"
              value={data.initiative}
              onChange={e => setData(prev => ({ ...prev, initiative: e.target.value }))}
              style={{ width: '100%', marginBottom: '5px' }}
            />
            <label style={medievalFont}>Speed:</label>
            <input
              type="text"
              value={data.speed}
              onChange={e => setData(prev => ({ ...prev, speed: e.target.value }))}
              style={{ width: '100%', marginBottom: '5px' }}
            />
            <label style={medievalFont}>Hit Points:</label>
            <input
              type="text"
              value={data.hitPoints}
              onChange={e => setData(prev => ({ ...prev, hitPoints: e.target.value }))}
              style={{ width: '100%', marginBottom: '5px' }}
            />
          </div>
        )}
      </div>

      
      <div style={{
        borderTop: '1px solid #ccc',
        padding: '10px 0',
        backgroundColor: '#f5f5f5'
      }}>
        <button onClick={handleSave} style={{ width: '100%', fontFamily: 'Cinzel, serif' }}>
          💾 Save Changes
        </button>
      </div>
    </div>
  );
}

export default CharacterSheet;
