import React from 'react';
import { useDrag } from 'react-dnd';

const Icon = ({ icon }) => {
  const isPlaced = icon.left !== undefined && icon.top !== undefined;

  const [{ isDragging }, drag] = useDrag({
    type: 'ICON',
    item: {
      id: icon.id,
      left: icon.left ?? 500, // default starting point for new drops
      top: icon.top ?? 400
    },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  });

  const baseStyle = {
    width: '30px',
    height: '30px',
    opacity: isDragging ? 0.5 : 1,
    cursor: 'grab',
    borderRadius: '4px',
    pointerEvents: 'auto'
  };

  const positionedStyle = isPlaced
    ? {
        position: 'absolute',
        left: icon.left,
        top: icon.top,
        ...baseStyle,
        width: '40px',
        height: '40px',
        cursor: 'move',
      }
    : baseStyle;

  return (
    <img
      ref={drag}
      src={icon.src}
      alt={icon.alt}
      style={positionedStyle}
      draggable={false}
    />
  );
};

export default Icon;
