import React, { useEffect } from 'react';
import { useDrag } from 'react-dnd';
import { getEmptyImage } from 'react-dnd-html5-backend';

const Icon = ({ icon }) => {
  const initialX = icon.left ?? 500;
  const initialY = icon.top ?? 400;
  const isPlaced = typeof icon.left === 'number' && typeof icon.top === 'number';

  const [{ isDragging }, drag, preview] = useDrag(() => ({
    type: 'ICON',
    item: {
      id: icon.id,
      src: icon.src,
      alt: icon.alt,
      left: initialX,
      top: initialY
    },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }), [icon]);

  useEffect(() => {
    preview(getEmptyImage(), { captureDraggingState: true });
  }, [preview]);

  const wrapperStyle = isPlaced
    ? {
        position: 'absolute',
        left: icon.left,
        top: icon.top,
        width: '40px',
        height: '40px',
        zIndex: 2,
        cursor: 'move',
      }
    : {
        width: '30px',
        height: '30px',
        cursor: 'grab',
      };

  const imgStyle = {
    width: '100%',
    height: '100%',
    borderRadius: '4px',
    opacity: isDragging ? 0.5 : 1,
    pointerEvents: 'auto',
  };

  return (
    <div ref={drag} style={wrapperStyle}>
      <img
        src={icon.src}
        alt={icon.alt}
        style={imgStyle}
        draggable={false}
      />
    </div>
  );
};

export default Icon;
