import React from 'react';

export default function SideMenu({ activeTab, setActiveTab }) {
  const tabs = [
    { id: 'chat', icon: 'üí¨' },
    { id: 'inventory', icon: 'üéí' },
    { id: 'character', icon: 'üßù' },
    { id: 'map', icon: 'üó∫Ô∏è' },
    { id: 'spells', icon: 'üßô‚Äç‚ôÇÔ∏è' },
  ];

  return (
    <div style={{
      display: 'flex',
      justifyContent: 'space-around',
      backgroundColor: '#e6e6e6',
      borderBottom: '1px solid #ccc',
      padding: '10px 0'
    }}>
      {tabs.map(tab => (
        <button
          key={tab.id}
          onClick={() => setActiveTab(tab.id)}
          style={{
            fontSize: '20px',
            backgroundColor: 'transparent',
            border: 'none',
            cursor: 'pointer',
            color: activeTab === tab.id ? '#c00' : '#333'
          }}
        >
          {tab.icon}
        </button>
      ))}
    </div>
  );
}
