// SideMenu.js
import React from 'react';

export default function SideMenu({ activeTab, setActiveTab }) {
  const tabs = [
    { id: 'chat', label: 'üí¨ Chat' },
    { id: 'inventory', label: 'üéí Inventory' },
    { id: 'task', label: 'üìù Tasks' }
  ];

  return (
<div style={{ width: '300px', boxSizing: 'border-box', display: 'flex', flexDirection: 'column' }}>
  <SideMenu activeTab={activeTab} setActiveTab={setActiveTab} />
  <div style={{ flex: 1, overflow: 'hidden', padding: '10px' }}>
    {activeTab === 'inventory' && (
      <InventoryGrid inventory={inventory} handleUseItem={handleUseItem} />
    )}
    {activeTab === 'chat' && (
      <Chat room={sessionId} username={username} />
    )}
    {activeTab === 'task' && (
      <div>
        <h3>üìù Tasks (Coming Soon)</h3>
        <p>Feature under construction.</p>
      </div>
    )}
  </div>
</div>

  );
}
