import React, { useRef } from 'react';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

function CharacterSheet() {
  const sheetRef = useRef();

  const handleDownloadPDF = async () => {
    const canvas = await html2canvas(sheetRef.current);
    const imgData = canvas.toDataURL('image/png');
    const pdf = new jsPDF('p', 'mm', 'a4');
    const width = pdf.internal.pageSize.getWidth();
    const height = (canvas.height * width) / canvas.width;
    pdf.addImage(imgData, 'PNG', 0, 0, width, height);
    pdf.save('CharacterSheet.pdf');
  };

  return (
    <div style={{ fontFamily: 'Cinzel, serif' }}>
      <div ref={sheetRef} style={{ padding: '20px', backgroundColor: '#fff', border: '1px solid #ccc' }}>
        <h2 style={{ textAlign: 'center' }}>üßù Character Sheet</h2>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '10px' }}>
          {['STR', 'DEX', 'CON', 'INT', 'WIS', 'CHA'].map((stat) => (
            <div key={stat}>
              <label>{stat}</label>
              <input type="number" placeholder="10" style={{ width: '100%' }} />
            </div>
          ))}
        </div>

        <h4 style={{ marginTop: '20px' }}>üõ°Ô∏è Combat Stats</h4>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '10px' }}>
          <div><label>Armor Class</label><input type="number" style={{ width: '100%' }} /></div>
          <div><label>Initiative</label><input type="number" style={{ width: '100%' }} /></div>
          <div><label>Speed</label><input type="number" style={{ width: '100%' }} /></div>
          <div><label>Hit Points</label><input type="text" placeholder="max / current" style={{ width: '100%' }} /></div>
        </div>

        <h4 style={{ marginTop: '20px' }}>üéØ Saving Throws</h4>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '10px' }}>
          {['STR', 'DEX', 'CON', 'INT', 'WIS', 'CHA'].map((save) => (
            <div key={save + '-save'}>
              <label>{save} Save</label>
              <input type="number" style={{ width: '100%' }} />
            </div>
          ))}
        </div>

        <h4 style={{ marginTop: '20px' }}>üß† Skills</h4>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '10px' }}>
          {['Acrobatics', 'Stealth', 'Perception', 'Arcana', 'Survival', 'Persuasion'].map((skill) => (
            <div key={skill}>
              <label>{skill}</label>
              <input type="number" style={{ width: '100%' }} />
            </div>
          ))}
        </div>
      </div>

      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        <button onClick={handleDownloadPDF} style={{ padding: '10px 20px', fontWeight: 'bold' }}>üìÑ Download as PDF</button>
      </div>
    </div>
  );
}

export default CharacterSheet;
