import React, { useEffect } from 'react';
import { useDrop } from 'react-dnd';
import socket from './socket';
import Icon from './Icon';

const BattleMap = ({ mapImage, icons, setIcons }) => {
  const room = window.location.pathname.split("/").pop();

  const [, drop] = useDrop({
    accept: 'ICON',
    drop: (item, monitor) => {
      const delta = monitor.getDifferenceFromInitialOffset();
      const initialLeft = item.left ?? 500;
      const initialTop = item.top ?? 400;
      const newLeft = Math.round(initialLeft + (delta?.x || 0));
      const newTop = Math.round(initialTop + (delta?.y || 0));

      setIcons(prev => {
        const exists = prev.find(i => i.id === item.id);
        if (exists) {
          return prev.map(i =>
            i.id === item.id ? { ...i, left: newLeft, top: newTop } : i
          );
        } else {
          return [...prev, { ...item, left: newLeft, top: newTop }];
        }
      });

      socket.emit("moveIcon", {
        room,
        iconId: item.id,
        newPosition: { left: newLeft, top: newTop }
      });
    },
  });

  useEffect(() => {
    socket.on("iconMoved", ({ iconId, newPosition }) => {
      setIcons(prev => {
        const exists = prev.find(i => i.id === iconId);
        if (exists) {
          return prev.map(i =>
            i.id === iconId ? { ...i, ...newPosition } : i
          );
        } else {
          return [...prev, { id: iconId, ...newPosition }];
        }
      });
    });

    return () => {
      socket.off("iconMoved");
    };
  }, [setIcons]);

  return (
    <div
      ref={drop}
      style={{
        position: 'relative',
        width: '100%',
        height: '100%',
        maxWidth: '100%',
        maxHeight: '100%',
        overflow: 'hidden',
      }}
    >
      <img
        src={mapImage}
        alt="Battle Map"
        style={{
          width: '100%',
          height: '100%',
          objectFit: 'contain',
          display: 'block',
        }}
      />
      {icons.map(icon => (
        <Icon key={icon.id} icon={icon} />
      ))}
    </div>
  );
};

export default BattleMap;
