import React, { useEffect } from 'react';
import { useDrop } from 'react-dnd';
import socket from './socket';
import Icon from './Icon';

const BattleMap = ({ mapImage, icons, setIcons }) => {
  const room = window.location.pathname.split("/").pop();

  const [, drop] = useDrop({
    accept: 'ICON',
    drop: (item, monitor) => {
      const dropTargetRect = monitor.getDropTargetRect();
      const clientOffset = monitor.getClientOffset();

      if (!dropTargetRect || !clientOffset) return;

      const newLeft = Math.round(clientOffset.x - dropTargetRect.left);
      const newTop = Math.round(clientOffset.y - dropTargetRect.top);

      setIcons(prev => {
        const exists = prev.find(i => i.id === item.id && i.left !== undefined);
        if (exists) {
          const updated = prev.map(i =>
            i.id === item.id ? { ...i, left: newLeft, top: newTop } : i
          );

          socket.emit("moveIcon", {
            room,
            iconId: item.id,
            newPosition: { left: newLeft, top: newTop }
          });

          return updated;
        } else {
          const newIcon = { ...item, left: newLeft, top: newTop };

          socket.emit("moveIcon", {
            room,
            iconId: item.id,
            newPosition: { left: newLeft, top: newTop }
          });

          return [...prev, newIcon];
        }
      });
    },
  });

  useEffect(() => {
    socket.on("iconMoved", ({ iconId, newPosition }) => {
      setIcons(prev =>
        prev.map(icon =>
          icon.id === iconId ? { ...icon, ...newPosition } : icon
        )
      );
    });

    return () => {
      socket.off("iconMoved");
    };
  }, [setIcons]);

  return (
    <div
      ref={drop}
      style={{
        position: 'relative',
        width: '100%',
        height: '100%',
        overflow: 'hidden',
      }}
    >
      <img
        src={mapImage}
        alt="Battle Map"
        style={{
          width: '100%',
          height: '100%',
          objectFit: 'contain',
          display: 'block',
        }}
      />

      {/* Placed icons */}
      {icons
        .filter(icon => typeof icon.left === 'number' && typeof icon.top === 'number')
        .map(icon => (
          <Icon key={icon.id} icon={icon} />
        ))}

      {/* Toolbox: unplaced icons */}
      <div style={{
        position: 'absolute',
        bottom: '10px',
        right: '10px',
        display: 'flex',
        gap: '10px',
        zIndex: 5
      }}>
        {icons
          .filter(icon => typeof icon.left !== 'number' || typeof icon.top !== 'number')
          .map(icon => (
            <Icon key={icon.id} icon={icon} />
          ))}
      </div>
    </div>
  );
};

export default BattleMap;
